export function createCardBrandIcon(cardBrand: CardBrand): SVGSVGElement {
  switch (cardBrand) {
    case CardBrand.AMEX:
      return createAmexIcon();
    case CardBrand.DINERS:
      return createDinersIcon();
    case CardBrand.DISCOVER:
      return createDiscoverIcon();
    case CardBrand.JCB:
      return createJcbIcon();
    case CardBrand.MASTERCARD:
      return createMasterCardIcon();
    case CardBrand.UNIONPAY:
      return createUnionPay();
    case CardBrand.VISA:
      return createVisaIcon();
    default:
      return createDefaultCardIcon();
  }
}
